CREATE DATABASE investigadores;

USE investigadores;

CREATE TABLE facultad
	(
		cod int,
		nombre varchar(100),
		PRIMARY KEY (cod)
	);

CREATE TABLE investigador
	(
		dni int,
		nombre varchar(100),
		facultad int,
		PRIMARY KEY (dni)
	);

CREATE TABLE equipo
	(
		nroserie int,
		nombre varchar(100),
		facultad int,
		PRIMARY KEY (nroserie)
	);

CREATE TABLE reserva
	(
		investigador int,
		equipo int,
		inicio DATE,
		fin DATE
	);





SELECT * FROM facultad;
SELECT * FROM investigador;
SELECT * FROM equipo;
